const R = require('ramda')

/**
 * The factory function to construct a single pair
 * @function
 * @param first {any}
 * @param second {any}
 * @example
 * const pair=Pair('name','jituanlin')
 * //=> ['name','jituanlin']
 * */
const Pair = R.curry(
	(first, second) => [first, second]
)

module.exports = Pair