const R =require('ramda')
const isSupperSet =require('./isSuperSet')

/**
 * Compare whether two set (es6: new Set(iterable)) is same as.
 * @function
 * @param {Set} set1
 * @param {Set} set2
 * @return {boolean}
 * @example
 * j.setEq(
 *    new Set([1, 2, 3]),
 *    new Set([3, 2, 1])
 *) // return true
 *
 * j.setEq(
 *    new Set([1, 2]),
 *      new Set([1, 2, 3])
 * )// return false
 * */
const setEq = R.curry(
	(set1, set2) => set2.size === set1.size && isSupperSet(set1, set2)
)

module.exports= setEq