const j = require('../index')
const assert = require('assert')
const R =require('ramda')

describe(
	'midFlow function',
	() => it(
		'Matcher must same as ramda\'s cond function,but more readable',
		() => assert.deepEqual(
			j.Matcher(
				j.Pair(R.is(Array), R.head),
				j.Pair(R.T, R.always('no match'))
			)([1,2,3]),
			1
		)
	)
)