const j = require('../index')
const assert = require('assert')

describe(
	'setEquals function',
	() => {
		it(
			'two set with same items must be judged equals',
			() => assert.equal(
				j.setEq(
					new Set([1, 2, 3]),
					new Set([3, 2, 1])
				),
				true
			)
		)

		it(
			'two set with not same items must be judged not equals',
			() => assert.equal(
				j.setEq(
					new Set([1, 2]),
					new Set([1, 2, 3])
				),
				false
			)
		)
	}
)